import { useState } from 'react';
import profileIcon from '../assets/profileIcon.svg'

const Bio = () => {
    const [userDetails, setUserDetails] = useState({
        name:'Ebenezer Done',
        about:'Building Newdev.io - Learn to  code and connect with the best minds.',
        
    })

    const updateUserDetails = (event) => {
        event.preventDefault()
        setUserDetails({
            name: event.t
        })
    }

    const editForm = (
        <form className="edit-bio-form">
            <input type="text" id="" placeholder="Your name" />
            <input type="text" id="" placeholder="About You" />
            <br />
            <button type="button" className="cancel-button">
                cancel
            </button>
            <button type="buttin">save</button>
        </form>
    )
    return (
        <section className="bio">
            <div className="profile-photo" role="button" title="Click to edit photo">
                <img src={profileIcon} alt="profile" />
            </div>
            <div className="profile-info">
                <p className="name">{userDetails.name}</p>
                <p className="about">{userDetails.about}</p>
            <button>Edit</button>
            {editForm}
            </div>
        </section>
    )
}
 
export default Bio;
